contracts/FeePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./IERC20.sol";

/**
 * @title FeePool
 * @dev Public fee pool that collects swap fees and distributes rewards to prediction winners
 */
contract FeePool {
    address public owner;
    address public ammContract;
    address public predictionContract;

    // Token balances in the pool
    mapping(address => uint256) public poolBalances;

    // Allowed tokens
    mapping(address => bool) public allowedTokens;
    address[] public tokenList;

    // Events
    event FeeDeposited(address indexed token, uint256 amount, address indexed from);
    event RewardDistributed(address indexed winner, address indexed token, uint256 amount);
    event TokenAdded(address indexed token);
    event ContractUpdated(string contractType, address newAddress);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyAuthorized() {
        require(
            msg.sender == ammContract || 
            msg.sender == predictionContract || 
            msg.sender == owner,
            "Not authorized"
        );
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    /**
     * @dev Add a token to the allowed list
     */
    function addAllowedToken(address token) external onlyOwner {
        require(token != address(0), "Invalid token");
        require(!allowedTokens[token], "Token already added");

        allowedTokens[token] = true;
        tokenList.push(token);

        emit TokenAdded(token);
    }

    /**
     * @dev Set the AMM contract address
     */
    function setAMMContract(address _amm) external onlyOwner {
        require(_amm != address(0), "Invalid address");
        ammContract = _amm;
        emit ContractUpdated("AMM", _amm);
    }

    /**
     * @dev Set the Prediction contract address
     */
    function setPredictionContract(address _prediction) external onlyOwner {
        require(_prediction != address(0), "Invalid address");
        predictionContract = _prediction;
        emit ContractUpdated("Prediction", _prediction);
    }

    /**
     * @dev Deposit fees into the pool (called by AMM on swaps)
     */
    function depositFee(address token, uint256 amount) external onlyAuthorized {
        require(allowedTokens[token], "Token not allowed");
        require(amount > 0, "Amount must be > 0");

        // Transfer tokens from sender
        require(
            IERC20(token).transferFrom(msg.sender, address(this), amount),
            "Transfer failed"
        );

        poolBalances[token] += amount;

        emit FeeDeposited(token, amount, msg.sender);
    }

    /**
     * @dev Direct deposit (for manual deposits)
     */
    function deposit(address token, uint256 amount) external {
        require(allowedTokens[token], "Token not allowed");
        require(amount > 0, "Amount must be > 0");

        require(
            IERC20(token).transferFrom(msg.sender, address(this), amount),
            "Transfer failed"
        );

        poolBalances[token] += amount;

        emit FeeDeposited(token, amount, msg.sender);
    }

    /**
     * @dev Get pool balance for a token
     */
    function getPoolBalance(address token) external view returns (uint256) {
        return poolBalances[token];
    }

    /**
     * @dev Distribute 10% of pool balance to prediction winner
     */
    function distributeReward(address winner, address token) external onlyAuthorized returns (uint256) {
        require(winner != address(0), "Invalid winner");
        require(allowedTokens[token], "Token not allowed");

        uint256 poolBalance = poolBalances[token];
        if (poolBalance == 0) return 0;

        // Calculate 10% reward
        uint256 reward = poolBalance / 10;

        if (reward > 0) {
            poolBalances[token] -= reward;
            require(IERC20(token).transfer(winner, reward), "Transfer failed");

            emit RewardDistributed(winner, token, reward);
        }

        return reward;
    }

    /**
     * @dev Get all pool balances
     */
    function getAllPoolBalances() external view returns (address[] memory tokens, uint256[] memory balances) {
        uint256 length = tokenList.length;
        tokens = new address[](length);
        balances = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            tokens[i] = tokenList[i];
            balances[i] = poolBalances[tokenList[i]];
        }

        return (tokens, balances);
    }

    /**
     * @dev Get token list
     */
    function getTokenList() external view returns (address[] memory) {
        return tokenList;
    }

    /**
     * @dev Emergency withdraw (only owner)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(poolBalances[token] >= amount, "Insufficient balance");
        poolBalances[token] -= amount;
        require(IERC20(token).transfer(owner, amount), "Transfer failed");
    }
}

contracts/IERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contracts/MiniAMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./IERC20.sol";
import "./FeePool.sol";

/**
 * @title MiniAMM
 * @dev Simplified Uniswap V2 style AMM with x * y = k formula
 * Supports multiple token pairs with 0.3% swap fee
 */
contract MiniAMM {
    address public owner;
    FeePool public feePool;

    // Fee: 0.3% = 3/1000
    uint256 public constant FEE_NUMERATOR = 3;
    uint256 public constant FEE_DENOMINATOR = 1000;

    // Minimum liquidity to prevent division by zero
    uint256 public constant MINIMUM_LIQUIDITY = 1000;

    // Pair structure
    struct Pair {
        address tokenA;
        address tokenB;
        uint256 reserveA;
        uint256 reserveB;
        uint256 totalLiquidity;
        bool exists;
    }

    // Liquidity provider balances
    struct LiquidityPosition {
        uint256 liquidity;
        uint256 depositedA;
        uint256 depositedB;
    }

    // Pair ID => Pair data
    mapping(bytes32 => Pair) public pairs;
    bytes32[] public pairIds;

    // Pair ID => User => Liquidity position
    mapping(bytes32 => mapping(address => LiquidityPosition)) public liquidityPositions;

    // Statistics
    uint256 public totalSwapVolume;
    uint256 public totalFeesCollected;

    // Events
    event PairCreated(bytes32 indexed pairId, address tokenA, address tokenB);
    event LiquidityAdded(bytes32 indexed pairId, address indexed provider, uint256 amountA, uint256 amountB, uint256 liquidity);
    event LiquidityRemoved(bytes32 indexed pairId, address indexed provider, uint256 amountA, uint256 amountB, uint256 liquidity);
    event Swap(bytes32 indexed pairId, address indexed user, address tokenIn, address tokenOut, uint256 amountIn, uint256 amountOut, uint256 fee);

    // Reentrancy guard
    bool private locked;
    modifier nonReentrant() {
        require(!locked, "Reentrancy");
        locked = true;
        _;
        locked = false;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    constructor(address _feePool) {
        owner = msg.sender;
        feePool = FeePool(_feePool);
    }

    /**
     * @dev Generate pair ID from two token addresses
     */
    function getPairId(address tokenA, address tokenB) public pure returns (bytes32) {
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        return keccak256(abi.encodePacked(token0, token1));
    }

    /**
     * @dev Create a new trading pair
     */
    function createPair(address tokenA, address tokenB) external onlyOwner returns (bytes32) {
        require(tokenA != tokenB, "Identical tokens");
        require(tokenA != address(0) && tokenB != address(0), "Zero address");

        bytes32 pairId = getPairId(tokenA, tokenB);
        require(!pairs[pairId].exists, "Pair exists");

        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);

        pairs[pairId] = Pair({
            tokenA: token0,
            tokenB: token1,
            reserveA: 0,
            reserveB: 0,
            totalLiquidity: 0,
            exists: true
        });

        pairIds.push(pairId);

        emit PairCreated(pairId, token0, token1);
        return pairId;
    }

    /**
     * @dev Add liquidity to a pair
     */
    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountA,
        uint256 amountB
    ) external nonReentrant returns (uint256 liquidity) {
        require(amountA > 0 && amountB > 0, "Amounts must be > 0");

        bytes32 pairId = getPairId(tokenA, tokenB);
        Pair storage pair = pairs[pairId];
        require(pair.exists, "Pair does not exist");

        // Determine correct order
        (uint256 amount0, uint256 amount1) = tokenA < tokenB 
            ? (amountA, amountB) 
            : (amountB, amountA);

        // Transfer tokens
        require(
            IERC20(pair.tokenA).transferFrom(msg.sender, address(this), amount0),
            "Transfer A failed"
        );
        require(
            IERC20(pair.tokenB).transferFrom(msg.sender, address(this), amount1),
            "Transfer B failed"
        );

        // Calculate liquidity tokens
        if (pair.totalLiquidity == 0) {
            // First liquidity provider
            liquidity = sqrt(amount0 * amount1) - MINIMUM_LIQUIDITY;
            require(liquidity > 0, "Insufficient initial liquidity");
        } else {
            // Subsequent providers - proportional to existing liquidity
            uint256 liquidityA = (amount0 * pair.totalLiquidity) / pair.reserveA;
            uint256 liquidityB = (amount1 * pair.totalLiquidity) / pair.reserveB;
            liquidity = liquidityA < liquidityB ? liquidityA : liquidityB;
        }

        require(liquidity > 0, "Insufficient liquidity minted");

        // Update state
        pair.reserveA += amount0;
        pair.reserveB += amount1;
        pair.totalLiquidity += liquidity;

        LiquidityPosition storage position = liquidityPositions[pairId][msg.sender];
        position.liquidity += liquidity;
        position.depositedA += amount0;
        position.depositedB += amount1;

        emit LiquidityAdded(pairId, msg.sender, amount0, amount1, liquidity);

        return liquidity;
    }

    /**
     * @dev Remove liquidity from a pair
     */
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidityAmount
    ) external nonReentrant returns (uint256 amountA, uint256 amountB) {
        bytes32 pairId = getPairId(tokenA, tokenB);
        Pair storage pair = pairs[pairId];
        require(pair.exists, "Pair does not exist");

        LiquidityPosition storage position = liquidityPositions[pairId][msg.sender];
        require(position.liquidity >= liquidityAmount, "Insufficient liquidity");
        require(liquidityAmount > 0, "Amount must be > 0");

        // Calculate token amounts to return
        uint256 amount0 = (liquidityAmount * pair.reserveA) / pair.totalLiquidity;
        uint256 amount1 = (liquidityAmount * pair.reserveB) / pair.totalLiquidity;

        require(amount0 > 0 && amount1 > 0, "Insufficient amounts");

        // Update state
        pair.reserveA -= amount0;
        pair.reserveB -= amount1;
        pair.totalLiquidity -= liquidityAmount;
        position.liquidity -= liquidityAmount;

        // Transfer tokens back
        require(IERC20(pair.tokenA).transfer(msg.sender, amount0), "Transfer A failed");
        require(IERC20(pair.tokenB).transfer(msg.sender, amount1), "Transfer B failed");

        emit LiquidityRemoved(pairId, msg.sender, amount0, amount1, liquidityAmount);

        return (amount0, amount1);
    }

    /**
     * @dev Swap tokens using x * y = k formula
     */
    function swap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) external nonReentrant returns (uint256 amountOut) {
        require(amountIn > 0, "Amount must be > 0");
        require(tokenIn != tokenOut, "Same token");

        bytes32 pairId = getPairId(tokenIn, tokenOut);
        Pair storage pair = pairs[pairId];
        require(pair.exists, "Pair does not exist");

        // Determine reserves based on token order
        (uint256 reserveIn, uint256 reserveOut) = tokenIn == pair.tokenA 
            ? (pair.reserveA, pair.reserveB) 
            : (pair.reserveB, pair.reserveA);

        require(reserveIn > 0 && reserveOut > 0, "Insufficient liquidity");

        // Calculate fee (0.3%)
        uint256 fee = (amountIn * FEE_NUMERATOR) / FEE_DENOMINATOR;
        uint256 amountInAfterFee = amountIn - fee;

        // Calculate output using x * y = k
        // (reserveIn + amountInAfterFee) * (reserveOut - amountOut) = reserveIn * reserveOut
        // amountOut = reserveOut - (reserveIn * reserveOut) / (reserveIn + amountInAfterFee)
        // amountOut = (amountInAfterFee * reserveOut) / (reserveIn + amountInAfterFee)
        amountOut = (amountInAfterFee * reserveOut) / (reserveIn + amountInAfterFee);

        require(amountOut > 0, "Insufficient output");
        require(amountOut < reserveOut, "Insufficient liquidity");

        // Transfer input tokens
        require(
            IERC20(tokenIn).transferFrom(msg.sender, address(this), amountIn),
            "Transfer in failed"
        );

        // Send fee to FeePool
        if (fee > 0) {
            IERC20(tokenIn).approve(address(feePool), fee);
            feePool.depositFee(tokenIn, fee);
            totalFeesCollected += fee;
        }

        // Transfer output tokens
        require(IERC20(tokenOut).transfer(msg.sender, amountOut), "Transfer out failed");

        // Update reserves
        if (tokenIn == pair.tokenA) {
            pair.reserveA += amountInAfterFee;
            pair.reserveB -= amountOut;
        } else {
            pair.reserveB += amountInAfterFee;
            pair.reserveA -= amountOut;
        }

        totalSwapVolume += amountIn;

        emit Swap(pairId, msg.sender, tokenIn, tokenOut, amountIn, amountOut, fee);

        return amountOut;
    }

    /**
     * @dev Get reserves for a pair
     */
    function getReserves(address tokenA, address tokenB) external view returns (uint256 reserveA, uint256 reserveB) {
        bytes32 pairId = getPairId(tokenA, tokenB);
        Pair storage pair = pairs[pairId];
        require(pair.exists, "Pair does not exist");

        if (tokenA == pair.tokenA) {
            return (pair.reserveA, pair.reserveB);
        } else {
            return (pair.reserveB, pair.reserveA);
        }
    }

    /**
     * @dev Get quote for swap
     */
    function getAmountOut(
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) external view returns (uint256 amountOut, uint256 fee) {
        bytes32 pairId = getPairId(tokenIn, tokenOut);
        Pair storage pair = pairs[pairId];
        require(pair.exists, "Pair does not exist");

        (uint256 reserveIn, uint256 reserveOut) = tokenIn == pair.tokenA 
            ? (pair.reserveA, pair.reserveB) 
            : (pair.reserveB, pair.reserveA);

        if (reserveIn == 0 || reserveOut == 0) {
            return (0, 0);
        }

        fee = (amountIn * FEE_NUMERATOR) / FEE_DENOMINATOR;
        uint256 amountInAfterFee = amountIn - fee;
        amountOut = (amountInAfterFee * reserveOut) / (reserveIn + amountInAfterFee);

        return (amountOut, fee);
    }

    /**
     * @dev Get all pairs
     */
    function getAllPairs() external view returns (bytes32[] memory) {
        return pairIds;
    }

    /**
     * @dev Get pair info
     */
    function getPairInfo(bytes32 pairId) external view returns (
        address tokenA,
        address tokenB,
        uint256 reserveA,
        uint256 reserveB,
        uint256 totalLiquidity
    ) {
        Pair storage pair = pairs[pairId];
        require(pair.exists, "Pair does not exist");
        return (pair.tokenA, pair.tokenB, pair.reserveA, pair.reserveB, pair.totalLiquidity);
    }

    /**
     * @dev Get user liquidity position
     */
    function getUserLiquidity(bytes32 pairId, address user) external view returns (
        uint256 liquidity,
        uint256 depositedA,
        uint256 depositedB
    ) {
        LiquidityPosition storage position = liquidityPositions[pairId][user];
        return (position.liquidity, position.depositedA, position.depositedB);
    }

    /**
     * @dev Square root function for liquidity calculation
     */
    function sqrt(uint256 x) internal pure returns (uint256 y) {
        if (x == 0) return 0;
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

    /**
     * @dev Update fee pool address
     */
    function setFeePool(address _feePool) external onlyOwner {
        require(_feePool != address(0), "Invalid address");
        feePool = FeePool(_feePool);
    }
}

contracts/PredictionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./IERC20.sol";
import "./FeePool.sol";

/**
 * @title PredictionMarket
 * @dev Prediction market for token price movements
 * Users bet on whether price will go up or down within a time period
 */
contract PredictionMarket {
    address public owner;
    FeePool public feePool;
    address public priceOracle; // Address authorized to submit prices

    // Bet structure
    struct Bet {
        address user;
        address token;
        uint256 amount;
        uint256 initialPrice;
        uint256 finalPrice;
        uint256 startTime;
        uint256 endTime;
        bool predictUp; // true = price goes up, false = price goes down
        BetStatus status;
    }

    enum BetStatus {
        Active,
        Won,
        Lost,
        Cancelled
    }

    // Allowed tokens for betting
    mapping(address => bool) public allowedTokens;
    address[] public tokenList;

    // Current prices (updated by oracle)
    mapping(address => uint256) public currentPrices;
    mapping(address => uint256) public lastPriceUpdate;

    // Bets
    mapping(uint256 => Bet) public bets;
    uint256 public betCounter;

    // User bets
    mapping(address => uint256[]) public userBets;

    // Limits
    uint256 public minBetAmount = 1e15; // 0.001 tokens
    uint256 public maxBetAmount = 1000e18; // 1000 tokens
    uint256 public minDuration = 60; // 1 minute
    uint256 public maxDuration = 86400; // 24 hours

    // Statistics
    uint256 public totalBets;
    uint256 public totalVolume;
    uint256 public activeBets;

    // Events
    event BetPlaced(uint256 indexed betId, address indexed user, address token, uint256 amount, bool predictUp, uint256 duration);
    event BetResolved(uint256 indexed betId, address indexed user, bool won, uint256 reward);
    event PriceUpdated(address indexed token, uint256 price, uint256 timestamp);
    event TokenAdded(address indexed token);

    // Reentrancy guard
    bool private locked;
    modifier nonReentrant() {
        require(!locked, "Reentrancy");
        locked = true;
        _;
        locked = false;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyOracle() {
        require(msg.sender == priceOracle || msg.sender == owner, "Only oracle");
        _;
    }

    constructor(address _feePool) {
        owner = msg.sender;
        priceOracle = msg.sender;
        feePool = FeePool(_feePool);
    }

    /**
     * @dev Add allowed token for betting
     */
    function addAllowedToken(address token) external onlyOwner {
        require(token != address(0), "Invalid token");
        require(!allowedTokens[token], "Token already added");

        allowedTokens[token] = true;
        tokenList.push(token);

        emit TokenAdded(token);
    }

    /**
     * @dev Set price oracle address
     */
    function setPriceOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "Invalid address");
        priceOracle = _oracle;
    }

    /**
     * @dev Update token price (called by oracle)
     */
    function updatePrice(address token, uint256 price) external onlyOracle {
        require(allowedTokens[token], "Token not allowed");
        require(price > 0, "Invalid price");

        currentPrices[token] = price;
        lastPriceUpdate[token] = block.timestamp;

        emit PriceUpdated(token, price, block.timestamp);
    }

    /**
     * @dev Batch update prices
     */
    function updatePrices(address[] calldata tokens, uint256[] calldata prices) external onlyOracle {
        require(tokens.length == prices.length, "Length mismatch");

        for (uint256 i = 0; i < tokens.length; i++) {
            if (allowedTokens[tokens[i]] && prices[i] > 0) {
                currentPrices[tokens[i]] = prices[i];
                lastPriceUpdate[tokens[i]] = block.timestamp;
                emit PriceUpdated(tokens[i], prices[i], block.timestamp);
            }
        }
    }

    /**
     * @dev Place a prediction bet
     */
    function placeBet(
        address token,
        uint256 amount,
        bool predictUp,
        uint256 duration
    ) external nonReentrant returns (uint256 betId) {
        require(allowedTokens[token], "Token not allowed");
        require(amount >= minBetAmount && amount <= maxBetAmount, "Invalid amount");
        require(duration >= minDuration && duration <= maxDuration, "Invalid duration");
        require(currentPrices[token] > 0, "Price not available");
        require(block.timestamp - lastPriceUpdate[token] < 3600, "Price too old");

        // Transfer bet amount
        require(
            IERC20(token).transferFrom(msg.sender, address(this), amount),
            "Transfer failed"
        );

        betId = betCounter++;

        bets[betId] = Bet({
            user: msg.sender,
            token: token,
            amount: amount,
            initialPrice: currentPrices[token],
            finalPrice: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + duration,
            predictUp: predictUp,
            status: BetStatus.Active
        });

        userBets[msg.sender].push(betId);
        totalBets++;
        totalVolume += amount;
        activeBets++;

        emit BetPlaced(betId, msg.sender, token, amount, predictUp, duration);

        return betId;
    }

    /**
     * @dev Resolve a bet after duration ends
     */
    function resolveBet(uint256 betId) external nonReentrant {
        Bet storage bet = bets[betId];
        require(bet.status == BetStatus.Active, "Bet not active");
        require(block.timestamp >= bet.endTime, "Bet not ended");
        require(currentPrices[bet.token] > 0, "Price not available");

        bet.finalPrice = currentPrices[bet.token];

        bool priceWentUp = bet.finalPrice > bet.initialPrice;
        bool won = (bet.predictUp && priceWentUp) || (!bet.predictUp && !priceWentUp);

        uint256 reward = 0;

        if (won) {
            bet.status = BetStatus.Won;

            // Return bet amount
            require(IERC20(bet.token).transfer(bet.user, bet.amount), "Transfer failed");

            // Get 10% from fee pool
            reward = feePool.distributeReward(bet.user, bet.token);
        } else {
            bet.status = BetStatus.Lost;

            // Send bet amount to fee pool
            IERC20(bet.token).approve(address(feePool), bet.amount);
            feePool.depositFee(bet.token, bet.amount);
        }

        activeBets--;

        emit BetResolved(betId, bet.user, won, reward);
    }

    /**
     * @dev Cancel bet (only before it ends, with penalty)
     */
    function cancelBet(uint256 betId) external nonReentrant {
        Bet storage bet = bets[betId];
        require(bet.user == msg.sender, "Not your bet");
        require(bet.status == BetStatus.Active, "Bet not active");
        require(block.timestamp < bet.endTime, "Bet already ended");

        bet.status = BetStatus.Cancelled;

        // Return 90% (10% penalty goes to fee pool)
        uint256 penalty = bet.amount / 10;
        uint256 refund = bet.amount - penalty;

        if (penalty > 0) {
            IERC20(bet.token).approve(address(feePool), penalty);
            feePool.depositFee(bet.token, penalty);
        }

        require(IERC20(bet.token).transfer(msg.sender, refund), "Transfer failed");

        activeBets--;

        emit BetResolved(betId, msg.sender, false, 0);
    }

    /**
     * @dev Get bet info
     */
    function getBet(uint256 betId) external view returns (
        address user,
        address token,
        uint256 amount,
        uint256 initialPrice,
        uint256 finalPrice,
        uint256 startTime,
        uint256 endTime,
        bool predictUp,
        BetStatus status
    ) {
        Bet storage bet = bets[betId];
        return (
            bet.user,
            bet.token,
            bet.amount,
            bet.initialPrice,
            bet.finalPrice,
            bet.startTime,
            bet.endTime,
            bet.predictUp,
            bet.status
        );
    }

    /**
     * @dev Get user's bets
     */
    function getUserBets(address user) external view returns (uint256[] memory) {
        return userBets[user];
    }

    /**
     * @dev Get active bets count
     */
    function getActiveBetsCount() external view returns (uint256) {
        return activeBets;
    }

    /**
     * @dev Get token price
     */
    function getPrice(address token) external view returns (uint256 price, uint256 lastUpdate) {
        return (currentPrices[token], lastPriceUpdate[token]);
    }

    /**
     * @dev Get all allowed tokens
     */
    function getAllowedTokens() external view returns (address[] memory) {
        return tokenList;
    }

    /**
     * @dev Update bet limits
     */
    function updateLimits(
        uint256 _minBet,
        uint256 _maxBet,
        uint256 _minDuration,
        uint256 _maxDuration
    ) external onlyOwner {
        minBetAmount = _minBet;
        maxBetAmount = _maxBet;
        minDuration = _minDuration;
        maxDuration = _maxDuration;
    }

    /**
     * @dev Set fee pool
     */
    function setFeePool(address _feePool) external onlyOwner {
        require(_feePool != address(0), "Invalid address");
        feePool = FeePool(_feePool);
    }
}

